! function() {
    "use strict";
    jQuery(window).on("elementor/frontend/init", () => {
        elementorFrontend.hooks.addAction("frontend/element_ready/spaciaz-testimonials.default", e => {
            elementorFrontend.elementsHandler.addHandler(spaciazSwiperBase, {
                $element: e
            }), jQuery(document).ready(function(o) {
                let n = o(".testimonial-layout-3");
                if (n.length) {
                    let e = n.find(".elementor-testimonial-item");

                    function t() {
                        let t = o(window).scrollTop() + o(window).height() / 2;
                        e.each(function() {
                            let e = o(this);
                            var n = e.offset().top;
                            t >= n ? e.addClass("active") : e.removeClass("active")
                        })
                    }
                    t(), o(window).on("scroll", function() {
                        t()
                    })
                }
            })
        })
    })
}();