! function(e) {
    "use strict";
    e(window).on("elementor/frontend/init", () => {
        elementorFrontend.hooks.addAction("frontend/element_ready/spaciaz-button-mousemove.default", t => {
            t.find(".movingButton").each(function() {
                e(this).on("mouseenter", function() {
                    let o = e(this).offset(),
                        n = e(this);
                    n.off("mousemove").on("mousemove", function(t) {
                        var e = t.pageX - o.left - n.outerWidth() / 2,
                            t = t.pageY - o.top - n.outerHeight() / 2,
                            e = Math.max(-20, Math.min(20, e)),
                            t = Math.max(-20, Math.min(20, t));
                        n.css({
                            transform: `translate3d(${e}px, ${t}px, 0px)`
                        })
                    })
                }), e(this).on("mouseleave", function() {
                    e(this).off("mousemove").css({
                        transform: "translate3d(0px, 0px, 0px)"
                    })
                })
            })
        })
    })
}(jQuery);